sp_widget.client_script:
function($scope,$rootScope) {
		/* widget controller */
		var c = this;

		$rootScope.$on('selectRecord', function(event,data) { 
			console.log('Listener caught NoteID: ' + $rootScope.someID);

			c.server.get({
				action: 'getImage',
				someID: $rootScope.someID
			}).then(function(r){
				c.data.caption = r.data.caption;
			})

		});
	}

sp_widget.script:
(function() {
	/* populate the 'data' object */
	/* e.g., data.table = $sp.getValue('table'); */
	if(input && input.someID){
		var galRec = new GlideRecord('x_68723_picwik_app_picwik_gallery');
		if (galRec.get(input.someID)) {
			if (input.action == 'getNote') {
				data.galRecords = {};
				$sp.getRecordValues(data.galRecords, galRec, "caption,image,sys_id");
			}
		}
	}
})();
